def demand(price: Int) -> Int {
    if price > 10 {
        80
    } else {
        120
    }
}

def main() -> Unit {
    let d = demand(12)
    println(d)
}

def optimize(cost: Int, carbon: Int) -> Int {
    if carbon < 8 {
        cost - 2
    } else {
        cost + 3
    }
}

def main() -> Unit {
    println(optimize(10, 6))
}

def total(a: Int, b: Int, c: Int) -> Int {
    a + b + c
}

def main() -> Unit {
    println(total(3, 7, 9))
}

def choose_strategy(load: Int) -> Int {
    if load > 70 {
        1
    } else {
        0
    }
}

def main() -> Unit {
    println(choose_strategy(82))
}
